/*jshint asi:true*/

module.exports = exports = Config

function Config(config, state){
  var isDynamicKey, realKey;
  for (var key in config) {
    isDynamicKey = key.charAt(0) === '$'
    
    state[key] = config[key];
    
    if (!isDynamicKey) continue;
    realKey = key.substring(1);
    state[realKey] = state[key]();
  }
}
