var createDOM = require('../')

require('tap').test(function tap(t){
  
  function quacksLikeA_PSD(t, document){
    t.test('document', function(t){
      t.is(typeof document, 'object', 'exists')
      t.is(typeof document.id, 'number', 'id')
      t.end()
    })
  }
  
  var dom = createDOM(require('./stuff/mockGenerator'))
  
  t.test('exists', function tap(t){
    t.is(typeof dom, 'object')
    t.end()
  })
  
  t.test('current document', function tap(t){
    dom.getCurrentDocument(function(error, document){
      t.notOk(error)
      quacksLikeA_PSD(t, document)
      t.end()
    })
  })
  
  t.test('current document', function tap(t){
    dom.getCurrentDocument(function(error, document){
      t.notOk(error)
      quacksLikeA_PSD(t, document)
      t.end()
    })
  })
  
  /*
    
  */
  
})
